function setupSearch(){const searchInput=document.getElementById("searchInput");const filterButtons=document.querySelectorAll(".filter-btn");let currentCategory="all";let currentSearchTerm="";window.currentCategory=currentCategory;window.currentSearchTerm=currentSearchTerm;if(!searchInput)return;function filterCards(){if(typeof aplicarTodosLosFiltros==="function"){aplicarTodosLosFiltros()}else{const cards=document.querySelectorAll(".card");let hasVisibleCards=false;cards.forEach(card=>{const title=card.querySelector("h4").textContent.toLowerCase();const cardCategory=card.getAttribute("data-category")||"platos";const matchesSearch=title.includes(currentSearchTerm);const matchesCategory=currentCategory==="all"||cardCategory===currentCategory;if(matchesSearch&&matchesCategory){card.classList.remove("hidden");hasVisibleCards=true}else{card.classList.add("hidden")}});const noResults=document.getElementById("noResults");if(!hasVisibleCards){if(!noResults){const noResultsElement=document.createElement("p");noResultsElement.id="noResults";noResultsElement.textContent="No se encontraron platos que coincidan con tu bÃºsqueda.";noResultsElement.style.textAlign="center";noResultsElement.style.margin="20px 0";noResultsElement.style.color="#666";document.querySelector(".menu").appendChild(noResultsElement)}}else if(noResults){noResults.remove()}}}searchInput.addEventListener("input",e=>{currentSearchTerm=e.target.value.toLowerCase();window.currentSearchTerm=currentSearchTerm;filterCards()});filterButtons.forEach(button=>{button.addEventListener("click",()=>{filterButtons.forEach(btn=>btn.classList.remove("active"));button.classList.add("active");currentCategory=button.getAttribute("data-category");window.currentCategory=currentCategory;filterCards()})});filterCards()}document.addEventListener("DOMContentLoaded",setupSearch);