function manejarClickCarrito(e){e.preventDefault();e.stopPropagation();const titulo=document.querySelector(".producto-info h1")?.textContent||"Producto";const precioTexto=document.querySelector(".producto-precio .precio-numero")?.textContent||"0";const precio=parseFloat(precioTexto.replace(/[^0-9.]/g,""))||0;let cantidad=parseInt(document.getElementById("cantidad").value)||1;const imagen=document.querySelector(".producto-imagen img")?.src||"";const urlParams=new URLSearchParams(window.location.search);const productoId=urlParams.get("id")||"producto";let carrito=JSON.parse(localStorage.getItem("carrito"))||[];const itemExistente=carrito.find(item=>item.id===productoId);if(itemExistente){itemExistente.cantidad=cantidad}else{carrito.push({id:productoId,nombre:titulo,precio:precio,imagen:imagen,cantidad:cantidad})}localStorage.setItem("carrito",JSON.stringify(carrito));const carritoContador=document.getElementById("carritoContador");if(carritoContador){const totalItems=carrito.reduce((total,item)=>total+item.cantidad,0);carritoContador.textContent=totalItems}const notificacion=document.createElement("div");notificacion.textContent=`ยก${cantidad} ${cantidad===1?"unidad":"unidades"} de ${titulo} ${cantidad===1?"agregada":"agregadas"} al carrito!`;notificacion.style.position="fixed";notificacion.style.bottom="20px";notificacion.style.right="20px";notificacion.style.backgroundColor="#4CAF50";notificacion.style.color="white";notificacion.style.padding="15px 25px";notificacion.style.borderRadius="5px";notificacion.style.zIndex="1000";notificacion.style.transition="opacity 0.5s";document.body.appendChild(notificacion);setTimeout(()=>{notificacion.style.opacity="0";setTimeout(()=>notificacion.remove(),500)},1500);setTimeout(()=>{window.location.href="menu.html"},1500)}document.addEventListener("DOMContentLoaded",function(){const btnCarrito=document.getElementById("btn-carrito");if(btnCarrito){btnCarrito.removeEventListener("click",manejarClickCarrito);btnCarrito.addEventListener("click",manejarClickCarrito)}const aumentarBtn=document.getElementById("aumentar");const disminuirBtn=document.getElementById("disminuir");const cantidadInput=document.getElementById("cantidad");if(aumentarBtn&&disminuirBtn&&cantidadInput){aumentarBtn.addEventListener("click",()=>{cantidadInput.value=parseInt(cantidadInput.value)+1});disminuirBtn.addEventListener("click",()=>{const valorActual=parseInt(cantidadInput.value);if(valorActual>1){cantidadInput.value=valorActual-1}})}});